<link rel="stylesheet" href="https://fonts.googleapis.com/css2?family=Material+Symbols+Outlined:opsz,wght,FILL,GRAD@48,500,1,200" />



<div class="area">

  <div class="custom-select"><select onfocus="this.size=5;"
      onblur="this.size=0;"
      onchange="this.size=1; this.blur()" id="character1"></select></div>
  <div class="custom-select"><select onfocus="this.size=5;"
      onblur="this.size=0;"
      onchange="this.size=1; this.blur()" id="character2"></select></div>

<div class="container" id="relationshipInfo"></div>
</div>



<script type="module" src="index.js"></script>

<style>
  /* body {
        user-select: none;
        -webkit-user-select: none;
        -moz-user-select: none;
        -ms-user-select: none;
    } */

  select {
    /*appearance: none;
    /* safari 
    -webkit-appearance: none;*/
    padding: 10px 12px;
    border-radius: 8px;
    width:100%;
  }

  .custom-select {
    max-height: 200px;
    min-height: 150px;
    overflow: hidden;
  }

  /*.custom-select::before,
  .custom-select::after {
    --size: 0.3rem;
    position: absolute;
    content: "";
    right: 1rem;
    pointer-events: none;
  }

  .custom-select::before {
    border-left: var(--size) solid transparent;
    border-right: var(--size) solid transparent;
    border-bottom: var(--size) solid black;
    top: 40%;
  }

  .custom-select::after {
    border-left: var(--size) solid transparent;
    border-right: var(--size) solid transparent;
    border-top: var(--size) solid black;
    top: 55%;
  }*/

  option:checked { 
    box-shadow: 0 0 10px 100px #5a5a5a inset;
    color: white;
  }

  option:hover {
    box-shadow: 0 0 10px 100px #B5EAEA inset;
    color: black !important;
  }

  .area {
    display: grid;
    grid-template-columns: minmax(20px, 1fr) minmax(20px, 1fr);
    justify-items: center;
    /*grid-row-gap: 50px;*/
  }


  .container {
    display: grid;
    grid-column: 1 / span 2;
  }


  tbody > tr > td:first-child {
    width: 120px !important;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  .rowHeader {
    user-select: none;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
  }

  /* Positioned better */
  tr.header {
    position:sticky;
    top: 0;
  }

  .line:hover {
    display: block;
    width: 100%;
    height:100%;
    background: linear-gradient(
      to top right, transparent calc(50% - 1px), lightgrey , transparent calc(50% + 1px));
  }

  .cell-output {
    width: 90vw;
  }

  .cell-output-display:not(.no-overflow-x){
    overflow-x: visible !important;
  }

  td {
    /* visibility: hidden; */
    padding: 0px !important;
    height: 100px;
    width : 50px !important;
    padding-inline: auto auto;
  }

  th {
    background-color: hsl(154, 65%, 89%);
    user-select: none;
  }

  /* Maybe can use :before to still display somtihing */
  .text {
      display: none;
      text-align: left;
  }

  td:hover{
    width: 450px !important;
    animation: expand 0.5s forwards linear;
  }

  td:hover > .text {
    display: inline;
    visibility: visible;
  }


  @keyframes expand{
    0%{width: 0 50px;}
    25% {width: 0 100px;}
    50%{width: 0 150px;}
    75% {width: 0 200px;}
    100%{width: 0 250px;}
  }


</style>

<script>

    document.addEventListener('contextmenu', function (e) {
        e.preventDefault();
    });

    let parent = document.querySelector('.header').parentElement;

while (parent) {
    const hasOverflow = getComputedStyle(parent).overflow;
    if (hasOverflow !== 'visible') {
        console.log(hasOverflow, parent);
    }
    parent = parent.parentElement;
}

</script>
